import HeadSelector from './FieldSelection';
import './Table.css';

function Table({data}) {

    const noOfColumns = (data) => {
        let noOfColumns = 0
        data.forEach((item) => {
            if (item.data.length > noOfColumns) {
                noOfColumns = item.data.length;
            }
        })
        console.log(noOfColumns);
        return noOfColumns;
    }

    // var cols = [];
    // for (var i = 0; i < noOfColumns; i++) {
    //     alert(i);
    //     // cols.push(<HeadSelector/>);
    // }

    const renderTableData = (object) => {
        return object.data.map((data,index) => {
            return  <td key={index}>{data}</td>;
        });
    }
    return (
        <>
            <table id="Info">
                <thead>
                    <tr>
                    <th>{noOfColumns(data)}</th>
                    </tr>
                </thead>
                <tbody>
                    { data &&   
                        data.map((value,index) => {
                            return <tr key={index}>{renderTableData(value)}</tr>;
                        })
                    }
                </tbody>
            </table>
        </>
    )
}
export default Table;
