import React, { Component, useState, useEffect  } from 'react';
import HeadSelector from './FieldSelection';
import './Table.css';

const buttonRef = React.createRef()

class Table extends Component {

    renderTableData = (object) => {
        return object.data.map((data,index) => {
            return  <td key={index}>{data}</td>;
        });
    }

    handleClick= (e) => {
        if(e.target.nodeName === 'INPUT' && e.target.parentElement.nodeName === 'TH'){
            let tbody = document.getElementsByTagName('tbody')[0],
            checks = [...tbody.getElementsByTagName('input')],
            rows = [...tbody.children];
            console.log(rows)
            rows.forEach((it)=>{
                it.classList.remove('selection');
            });
            if(e.target.checked){
               checks.forEach((item,index)=>{
                  item.checked = true;
                 rows[index].classList.add('selection');
               });              
            }
            else{
               checks.forEach((item,index)=>{
                  item.checked = false;
                 rows[index].classList.remove('selection');
               });
            }  
        }
    }

    handleSingleCheck = (event) => {
        if(event.shiftKey){
            event.target.parentElement.parentElement.classList.toggle('selection');
        }
        event.target.parentElement.parentElement.classList.toggle('selection');
    }

    handleDeleteClick = (e) => {
        // const removedData = {...newData}
        let rowstoremove = document.querySelectorAll('.selection').forEach(node =>{
            // console.log(data, node.dataset.index)
            // delete removedData[node.dataset.index]
            node.remove();
        });
        // setNewData(removedData);
        // console.log(newData)
    }

    handleExportClick = (e) => {
        const exportData = [...newData]
        let rowstoexport = document.querySelectorAll('.selection').forEach(node =>{
            console.log(node.dataset.index,data);
            if(data[node.dataset.index]){
                console.log(data[node.dataset.index]);
                exportData.push(data[node.dataset.index])
            }
        });
        setNewData(exportData,()=>{

        })
        console.log(exportData)
    }
    render() {
        return (
            <>
                <div>
                    <button onClick={(e) => handleDeleteClick(e)}>Delete Row(s)</button>
                    <button onClick={(e) => handleExportClick(e)}>Export Row(s)</button>
                </div>
                <table id="Info">
                    <thead>
                        <tr>
                            <th><input type="checkbox" onClick={(e) => handleClick(e)}/></th>
                            <th><HeadSelector/></th>
                            <th><HeadSelector/></th>
                            <th><HeadSelector/></th>
                            <th><HeadSelector/></th>
                        </tr>
                    </thead>
                    <tbody>
                        { data &&   
                            data.map((value,index) => {
                                return <tr key={index} data-index={index}>
                                        <td><input type="checkbox" onClick={(e) => handleSingleCheck(e)}/></td>
                                        {renderTableData(value)}
                                    </tr>;
                            })
                        }
                    </tbody>
                </table>
            </>
        )
    }
}
export default Table;
